    import React from 'react';
    

export default function HomeWhoWeHelp() {

    const [currentPage, setCurrentPage] = useState(1)
    const [itemsPerPage, setItemsPerPage] = useState(3)

    const indexOfLastItem = currentPage * itemsPerPage
    const indexOfFirstItem = indexOfLastItem - itemsPerPage
    const currentItems = data.slice(indexOfFirstItem, indexOfLastItem)

    const paginate = (pageNumber) => setCurrentPage(pageNumber)

    return (
        <section className="whoWeHelp" id="whoWeHelp">
            <div className="whoWeHelp-Container">
                <h2 className="whoWeHelp-Title">Komu pomagamy?</h2>
                <img className="separator" src={separator} alt="separator" />
                <div className="whoWeHelp-Text">
                    <p>
                        W naszej bazie znajdziesz listę zweryfikowanych Fundacji, z którymi
                        współpracujemy. Możesz sprawdzić czym się zajmują, komu pomagają i czego
                        potrzebują.
                    </p>
                </div>
                <div className="whoWeHelp-List">
                    <ul>
                        {currentItems.map((item) => (
                            <li key={item.id}>
                                <div className="whoWeHelp-List-Item">
                                    <img src={item.img} alt={item.alt} />
                                    <h3>{item.title}</h3>
                                    <p>{item.text}</p>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
                <Pagination
                    itemsPerPage={itemsPerPage}
                    totalItems={data.length}
                    paginate={paginate}
                />
            </div>
        </section>
    );
}
