import  { useState } from "react";
import OrganizationsVers from "./OrganizationsVers";

export default function WwhView({ institutions }) {
  const [active, setActive] = useState(0);
  const [activeOrg, setActiveOrg] = useState(0);

  const handleActive = (index) => {
    setActive(index);
  };

  const handleActiveOrg = (index) => {
    setActiveOrg(index);
  };

  return (
    <div className="wwh__view">

      <div className="wwh__view__buttons">
        {institutions.map((institution, index) => (
          <button
            key={index}
            className={
              active === index
                ? "wwh__view__buttons__button wwh__view__buttons__button--active"
                : "wwh__view__buttons__button"
            }
            onClick={() => handleActive(index)}
          >
            {institution.name}
          </button>
        ))}
      </div>

      <div className="wwh__view__organizations">
        {institutions[active].organizations.map((organization, index) => (
          <button
            key={index}
            className={
              activeOrg === index
                ? "wwh__view__organizations__organization wwh__view__organizations__organization--active"
                : "wwh__view__organizations__organization"
            }
            onClick={() => handleActiveOrg(index)}
          >
            {organization.name}
          </button>
        ))}
      </div>

      <OrganizationsVers
        organization={institutions[active].organizations[activeOrg]}
      />
    </div>
  );
}

