import  { useState } from "react";
import OrganizationsVers from "./OrganizationsVers";

export default function WwhView({ institutions }) {

  const [rowsNumber] =useState(3);
  const [currentPage, setCurrentPage] = useState(1);
  const[pageNumber] = useState(Math.ceil(institutions.length / rowsNumber));
  const[pageSet, setPageSet] = useState([institutions[0], institutions[1], institutions[2]]);

  const handlePageChange = (e) => {

    const clikedPage = Number(e.target.innerText);
    const lastItem = clikedPage * rowsNumber;
    const firstItem = lastItem - rowsNumber;
    const newPageSet = institutions.slice(firstItem, lastItem);

    setCurrentPage(clikedPage);
    setPageSet(newPageSet);

    const pages = document.querySelectorAll(".page");
    pages.forEach((page) => {
      page.classList.remove("active");
    });
    e.target.classList.add("active");
  };

  const handleNextPage = () => {
    if (currentPage < pageNumber) {
      const lastItem = (currentPage + 1) * rowsNumber;
      const firstItem = lastItem - rowsNumber;
      const newPageSet = institutions.slice(firstItem, lastItem);

      setCurrentPage(currentPage + 1);
      setPageSet(newPageSet);

      const pages = document.querySelectorAll(".page");
      pages.forEach((page) => {
        page.classList.remove("active");
      });
      pages[currentPage].classList.add("active");
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      const lastItem = (currentPage - 1) * rowsNumber;
      const firstItem = lastItem - rowsNumber;
      const newPageSet = institutions.slice(firstItem, lastItem);

      setCurrentPage(currentPage - 1);
      setPageSet(newPageSet);

      const pages = document.querySelectorAll(".page");
      pages.forEach((page) => {
        page.classList.remove("active");
      });
      pages[currentPage - 2].classList.add("active");
    }
  };


  return (
    <div className="wwhView">
      <OrganizationsVers pageSet={pageSet} />
      <div className="pagination">
        <button onClick={handlePrevPage} className="prevPage">
          <i className="fas fa-chevron-left"></i>
        </button>
        {institutions.map((institution, index) => {
          return (
            <button
              key={index}
              onClick={handlePageChange}
              className="page"
            >
              {index + 1}
            </button>
          );
        })}
        <button onClick={handleNextPage} className="nextPage">
          <i className="fas fa-chevron-right"></i>
        </button>
      </div>
    </div>
  );
}



  

